	modal.save = function(type, editid, _this,_tplid) {
		if(typeof(type) === 'undefined') {
			type = false;
		}
		if(type && modal.type == 5) {
			tip.msgbox.err("产品详情页涉及产品数据问题，请至手机端预览");
			
			return;
		}

		modal.data = {};
		modal.data = {
			page: modal.page,
			items: modal.items
		};
		if(!modal.page.title) {
			tip.msgbox.err("页面标题是必填项");
			$("#page").trigger("click");
			return;
		}
		_this.hasClass('save-page') ? _this.data('status', 1).text("保存中...") : '';
		//biz.mdurl("sudu8_page/Diy/make")
		$.post(save_url, {
			id: modal.id,
			data: modal.data,
		   siteid: modal.siteid,
		   tplid:_tplid
		}, function(ret) {
			if(ret.status == 0) {
				tip.msgbox.suc(ret.message);
				$(".btn-save[data-type='save']").text("保存页面").data("status", 0);
				$(".btn-save[data-type='savetemp']").text("另存为模板").data("status", 0);
				if(ret.id > 0){
					setTimeout(function () {
						window.location.href = window.location.href+"&key_id="+ret.id;
                    },1000);
				}
				return;
			}else{
                tip.msgbox.err(ret.message)
			}
			$(".btn-save[data-type='save']").text("保存页面").data("status", 0);
			$(".btn-save[data-type='savetemp']").text("另存为模板").data("status", 0);
		}, 'json')
	};
