CREATE TABLE IF NOT EXISTS `ims_wlmerchant_activity_category` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL, `aid` int(11) NOT NULL, `name` varchar(125) NOT NULL COMMENT '名称', `logo` varchar(255) NOT NULL COMMENT '图标', `sort` int(11) NOT NULL COMMENT '排序', `status` int(11) NOT NULL COMMENT '状态', `createtime` int(11) NOT NULL COMMENT '创建时间', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_wlmerchant_activity_record` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL, `aid` int(11) NOT NULL, `status` int(11) NOT NULL COMMENT '状态 1待使用 2已核销', `sid` int(11) NOT NULL COMMENT '商户id', `activityid` int(11) NOT NULL COMMENT '活动id', `mid` int(11) NOT NULL COMMENT '用户id', `checkcode` varchar(145) NOT NULL COMMENT '验证码', `usetimes` int(11) NOT NULL COMMENT '剩余使用次数', `usedtime` text NOT NULL COMMENT '核销记录', `createtime` int(11) NOT NULL COMMENT '创建时间', `orderid` int(11) DEFAULT NULL COMMENT '订单id', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_wlmerchant_activitylist` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL, `aid` int(11) NOT NULL, `sid` int(11) NOT NULL COMMENT '商户id', `cateid` int(11) NOT NULL COMMENT '分类id', `status` int(11) NOT NULL COMMENT '状态 0待审核 1未开始报名 2报名中 3已结束 4被驳回', `title` varchar(145) NOT NULL COMMENT '活动标题', `thumb` varchar(145) NOT NULL COMMENT '图片', `activestarttime` int(11) NOT NULL COMMENT '活动开始时间', `activeendtime` int(11) NOT NULL COMMENT '活动结束时间', `address` varchar(225) NOT NULL COMMENT '活动地址', `pv` int(11) NOT NULL COMMENT '活动浏览量', `sort` int(11) NOT NULL COMMENT '活动排序', `enrollstarttime` int(11) NOT NULL COMMENT '报名开始时间', `enrollendtime` int(11) NOT NULL COMMENT '报名结束时间', `maxpeoplenum` int(11) NOT NULL COMMENT '活动最大人数', `minpeoplenum` int(11) NOT NULL COMMENT '活动最小人数', `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '报名费', `vipstatus` int(11) NOT NULL COMMENT 'vip设置 0无 1特价 2特供', `vipprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'vip报名费', `settlementmoney` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '结算金额', `vipsettlementmoney` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'vip结算价', `isdistri` int(11) NOT NULL COMMENT '是否参与分销 1参与 0不参与', `onedismoney` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '一级分销', `twodismoney` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '二级分销', `threedismoney` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '三级分销', `threeurl` varchar(225) NOT NULL COMMENT '三方链接', `bgmusic` varchar(255) NOT NULL COMMENT '背景音乐', `enrolldetail` text NOT NULL COMMENT '报名须知', `detail` text NOT NULL COMMENT '活动详情', `share_title` varchar(32) NOT NULL COMMENT '分享标题', `share_desc` varchar(32) NOT NULL COMMENT '分享详情', `share_image` varchar(255) NOT NULL COMMENT '分享图片', `createtime` int(11) NOT NULL COMMENT '创建时间', `enrollnum` int(11) NOT NULL COMMENT '已经报名人数', `viponedismoney` decimal(10,2) NOT NULL, `viptwodismoney` decimal(10,2) NOT NULL, `vipthreedismoney` decimal(10,2) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
