l` text NOT NULL COMMENT '适用会员等级', `dissettime` int(11) NOT NULL COMMENT '分销佣金结算时间', `nostoreshow` int(11) NOT NULL COMMENT '不在商户详情页显示', `extflag` int(11) NOT NULL COMMENT '一般卡券0 外链卡券1', `extlink` varchar(500) NOT NULL COMMENT '外部链接', `extinfo` text NOT NULL COMMENT '外部信息', `salesmid` int(11) NOT NULL COMMENT '业务员mid', `name` varchar(145) NOT NULL DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;
