CREATE TABLE IF NOT EXISTS `ims_weisrc_dish_account` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属帐号', `storeid` varchar(1000) NOT NULL, `uid` int(10) unsigned NOT NULL DEFAULT '0', `from_user` varchar(100) NOT NULL DEFAULT '', `accountname` varchar(50) NOT NULL DEFAULT '', `password` varchar(200) NOT NULL DEFAULT '', `salt` varchar(10) NOT NULL DEFAULT '', `pwd` varchar(50) NOT NULL, `mobile` varchar(20) NOT NULL, `email` varchar(20) NOT NULL, `username` varchar(50) NOT NULL, `pay_account` varchar(200) NOT NULL, `displayorder` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序', `dateline` int(10) unsigned NOT NULL DEFAULT '0', `status` tinyint(1) unsigned NOT NULL DEFAULT '2' COMMENT '状态', `role` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1:店长,2:店员', `lastvisit` int(10) unsigned NOT NULL DEFAULT '0', `lastip` varchar(15) NOT NULL, `areaid` int(10) NOT NULL DEFAULT '0' COMMENT '区域id', `is_admin_order` tinyint(1) unsigned NOT NULL DEFAULT '1', `is_notice_order` tinyint(1) unsigned NOT NULL DEFAULT '1', `is_notice_queue` tinyint(1) unsigned NOT NULL DEFAULT '1', `is_notice_service` tinyint(1) unsigned NOT NULL DEFAULT '1', `is_notice_boss` tinyint(1) NOT NULL DEFAULT '0', `lat` decimal(18,10) NOT NULL DEFAULT '0.0000000000' COMMENT '经度', `lng` decimal(18,10) NOT NULL DEFAULT '0.0000000000' COMMENT '纬度', `remark` varchar(1000) NOT NULL DEFAULT '' COMMENT '备注', `schoolid` int(10) NOT NULL DEFAULT '0' COMMENT '学校id', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
